mapper.select:
SELECT user_num as "uesrNum", email, pw, tel, username, 
       gender, address, birth, created_at AS  "createdAt", updated_at AS "updatedAt"
  FROM APP_USER    
 where email = #{email} 

 	AND pw = #{pw}

mapper.update:
update APP_USER 
   set pw = #{newPw} 
 where email=#{email}
 AND pw =#{oldPw}

update APP_USER 
   set pw = #{newPw} 
 where user_num=#{userNum}
 AND pw =#{oldPw}

mapper.delete:
delete from APP_USER 
where


		email=#{email} and pw=#{pw}


		user_num = #{userNum}


		1 = 0
